    int res;
    
    
    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        limpiar();
        int bus=Integer.parseInt(Busqueda.getText());
        res=Instancias.getInstance().Buscar(bus);
        if(res!=-1){
            int num=Instancias.getInstance().getListacuenta(res).getNrocuenta();
            MOVIMIENTO mov[]=Instancias.getInstance().ConsultarcuentaM(num);
            for (int i = 0; i < Instancias.getInstance().getListacuenta(res).getPosicionactual(); i++) {
                Movimientos.setText(Movimientos.getText()+"ID Movimiento: "+mov[i].getIdmoviento()+"\n"
                        + "Fecha: "+mov[i].getFecha()+"\n"
                        + "Valor: "+mov[i].getValor()+"\n"
                        + "Tipo: "+mov[i].getTipo()+"\n"
                        + "Realizada por: "+mov[i].getQuienRealiza()+"\n\n");
            }
            Titular.setText(Instancias.getInstance().getListacuenta(res).getDatoscliente().getNombre());
            Ncuenta.setText(String.valueOf(Instancias.getInstance().getListacuenta(res).getNrocuenta()));
            Saldo.setText(String.valueOf(Instancias.getInstance().ConsultarcuentaS(num)));
        }else{
            JOptionPane.showMessageDialog(rootPane, "El numero de cuenta ingresado no se encuentra");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // TODO add your handling code here:
        limpiar();
        Instancias.getInstance().getConsultas().setVisible(false);
    }//GEN-LAST:event_jButton3ActionPerformed

    private void limpiar(){
        Movimientos.setText("");
        Ncuenta.setText("");
        Titular.setText("");
        Saldo.setText("");
    }
    
