    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        
        if (obj.vectorcreado()) {
            mostrarVector.setVisible(rootPaneCheckingEnabled);
            String dat="";
            for (int i = 0; i < 50; i++) {
                dat+=obj.getNombres(i)+" -> "+obj.getPuntuaciones(i)+"\n";
            }
            arreglo.setText(dat);
        }else{
            JOptionPane.showMessageDialog(rootPane, "Los datos no han sido agregados");
        }
        
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        // TODO add your handling code here:
        if (obj.listaCreada()) {
            mostrarLista.setVisible(rootPaneCheckingEnabled);
            Lista.setText(obj.toString());
        }else{
            JOptionPane.showMessageDialog(rootPane, "No se ha creado la lista");
        }
        
    }//GEN-LAST:event_jButton4ActionPerformed

