    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        obj.crearArreglos();
        JOptionPane.showMessageDialog(rootPane, "Datos agregados");
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // TODO add your handling code here:
        NodoLista aux;
        if (obj.vectorcreado()) {
            for (int i = 0; i < 50; i++) {
            aux=new NodoLista();
            if (obj.getPuntuaciones(i)!=0) {
                aux.setIndice(i);
                aux.setNombre(obj.getNombres(i));
                aux.setPuntuacion(obj.getPuntuaciones(i));
                obj.agregar(aux);
            }
        }
        JOptionPane.showMessageDialog(rootPane, "Lista creada");
        
        }else{
            JOptionPane.showMessageDialog(rootPane, "Se deben generar los datos antes de crear la lista");
        }
    }//GEN-LAST:event_jButton3ActionPerformed

