    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        for (int i = 0; i < notas.getTamaño(); i++) {
            datosEst aux = new datosEst();
            aux.setId(Integer.parseInt(JOptionPane.showInputDialog("Ingrese el id del estudiante "+(i+1))));
            aux.setNombre(JOptionPane.showInputDialog("Ingrese el nombre del estudiante "+(i+1)));
            aux.setApellido(JOptionPane.showInputDialog("Ingrese el apellido del estudiante "+(i+1)));
            
            do {
                aux.setN1(Float.parseFloat(JOptionPane.showInputDialog("Ingrese la nota 1 del estudiante "+(i+1))));
            } while (aux.getN1()<0 || aux.getN1()>5);
            
            do {
                aux.setN2(Float.parseFloat(JOptionPane.showInputDialog("Ingrese la nota 2 del estudiante "+(i+1))));
            } while (aux.getN2()<0 || aux.getN2()>5);
            
            do {
                aux.setN3(Float.parseFloat(JOptionPane.showInputDialog("Ingrese la nota 3 del estudiante "+(i+1))));
            } while (aux.getN3()<0 || aux.getN3()>5);
            notas.setVector(i, aux);
        }
        notas.calularDefinitiva();
        jButton2.setEnabled(true);
        
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        JOptionPane.showMessageDialog(rootPane, "Listado por ID de estudiantes reprobados: \n"+notas.listaReprobados());
        JOptionPane.showMessageDialog(rootPane, "Cantidad estudiantes aprobados: \n"+notas.numAprobados());
        JOptionPane.showMessageDialog(rootPane, "Promedio general del curso: \n"+notas.promGeneral());
        JOptionPane.showMessageDialog(rootPane, "Listado de nombres de estudiantes aprobados: \n"+notas.nombresAprobados());
        JOptionPane.showMessageDialog(rootPane, "Cuadro de honor: \n"+notas.cuadroHonor());
        JOptionPane.showMessageDialog(rootPane, "Notas por encima del promedio: \n"+notas.mayoresPromedio());
    }//GEN-LAST:event_jButton2ActionPerformed

